mapper.select:
        SELECT *
		FROM category_tb
		WHERE user_id = #{userId} AND category_id = #{categoryId}

SELECT * 
FROM category_tb 
WHERE category_title=#{categoryTitle} and user_id=#{userId}

mapper.update:
        UPDATE category_tb
		SET category_title = #{categoryTitle}
		WHERE user_id = #{userId} AND category_id=#{categoryId}
